import {Input, Output, EventEmitter} from '@angular/core';
import {HighlightService} from './HighlightService.js';

/**
 * The AbstractResourceList class implements methods common for upper level and nested resource lists.
 */
export class AbstractResourceList {
    /**
     * @property {string} caption           - the header of the list
     */
    @Input() caption = "Resources";
    /**
     * @property {Object} model  - the open-physiology model
     */
    @Input() model;
    /**
     * @property {Array<Resource>} items    - the list of displayed resources
     */
    @Input() items = [];
    /**
     * @property {Array<string>} types      - the list of displayed item types (resource classes)
     */
    @Input() types = [];

    @Input() options = {};

    @Input('selectedItem') set selectedItem(item) {
        if (this._selectedItem !== item) {
            this._selectedItem = item;
            this.selectedItemChange.emit(item);
        }
    }
    get selectedItem() { return this._selectedItem; }
    @Input('highlightedItem') set highlightedItem(item) {
        if (this._highlightedItem !== item) {
            this._highlightedItem = item;
            this.highlightedItemChange.emit(item);
            this._highlightService.highlight(this._highlightedItem);
        }
    }
    get highlightedItem() { return this._highlightedItem; }

    /**
     * @emits added                 - a new item added to the list
     */
    @Output() added = new EventEmitter();
    /**
     * @emits removed               - an item removed from the list
     */
    @Output() removed = new EventEmitter();
    /**
     * @emits updated               - an item in the list has been updated
     */
    @Output() updated = new EventEmitter();
    /**
     * @emits selectedItemChange    - the selected item changed
     */
    @Output() selectedItemChange = new EventEmitter();
    /**
     * @emits highlightedItemChange - the highlighted item changed
     */
    @Output() highlightedItemChange = new EventEmitter();

    _selectedItem    = null;
    _highlightedItem = null;
    _openItem        = null;

    _zones = [];

    _sortByMode   = "unsorted";
    _filterByMode = "Name";
    _searchString = "";

    /**
     * @param {HighlightService} highlightService - the service that notifies nested components about currently highlighted item
     */
    constructor(highlightService: HighlightService) {
        this._highlightService = highlightService;
        this._hs = this._highlightService.highlightedItemChanged$.subscribe(item => {
            if (this.items.includes(item) && this._highlightedItem !== item){
                this._highlightedItem = item;
            }
        })
    }

    ngOnInit() {
        if (!this.items) { this.items = []; }

        if (this.items[0] || !this.selectedItem) {
            this.selectedItem = this.items[0];
        }

        if (this.types.length === 0) {
            //If no specific classes were specified, it works with any class
            let visualClasses = [...this.model.Artefact.allSubclasses()].map(x => x.name);
            for (let cls of Object.values(this.model)){
                if (cls.isResource && !cls.abstract && !visualClasses.includes(cls.name)){
                    this.types.push(cls.name);
                    if (cls.name === this.model.Lyph.name){ this.types.push("LyphWithAxis"); }
                }
            }
        }
        this._zones = this.types.map(x => x + "_zone");
    }

    ngOnDestroy() {
        if (this._hs) {
            this._hs.unsubscribe();
        }
    }

    unhighlight(item) {
        if (this.highlightedItem === item) {
            this.highlightedItem = null;
        }
    }

    //Open item
    set openItem(item){
        if (this._openItem !== item) {
            this._openItem = item;
        }
    }

    get openItem(){
        return this._openItem;
    }

    /* Events */

    onSorted(prop: string) {
        this._sortByMode = prop.toLowerCase();
    }

    onFiltered(config) {
        this._filterByMode = config.mode.toLowerCase();
        this._searchString = config.filter;
    }

    onSaved(item, updatedItem) {
        this.updated.emit(this.items);
        if (item === this.selectedItem) {
            this.selectedItemChange.emit(this.selectedItem);
        }
    }

    onRemoved(item) {
        if (!this.items) return;
        let index = this.items.indexOf(item);
        if (index > -1) this.items.splice(index, 1);
        if (item === this.selectedItem) {
            if (this.items.length > 0){
                this.selectedItem = this.items[0];
            }
            else {
                this.selectedItem = null;
            }
        }
        item.delete();
        this.removed.emit(item);
        this.updated.emit(this.items);
    }

    onAdded(clsName) {
        let options = {};
        if (clsName === "LyphWithAxis") {
            clsName = this.model.Lyph.name;
            options.createAxis = true;
        }
        if (clsName === this.model.Lyph.name) {
            options.createRadialBorders = true;
        }

        let newItem = this.model[clsName].new({name: `New ${clsName}`}, options);

        if (clsName === this.model.Material.name) {
            let newType = this.model.Type.new({name: newItem.name, definition: newItem});
            newItem.p('name').subscribe(newType.p('name'));
        }

        this.items.push(newItem);
        this.updated.emit(this.items);
        this.added.emit(newItem);
        this.selectedItem = newItem;
    }

    /**
     * The getClassLabel function provides an human readable label for open-physiology class
     * @param {Resource} clsName - the resource class name
     * @returns {string} - the human readable label
     */
    getClassLabel(clsName: string): string{
        if (!clsName) { return ""; }
        let label = clsName;
        label = label.replace(/([a-z])([A-Z])/g, '$1 $2');
        label = label[0].toUpperCase() + label.substring(1).toLowerCase();
        return label;
    }

}

import {Injectable, EventEmitter} from '@angular/core';

/**
 * The HighlightService service notifies nested components about currently highlighted item
 */
@Injectable()
export class HighlightService {

    /**
     * @emits {EventEmitter} highlightedItemChanged$ - the event that the service emits when
     * a new item is highlighted in a nested resource widget
     */
    highlightedItemChanged$: EventEmitter;

    constructor() {
        this.highlightedItemChanged$ = new EventEmitter();
    }

    /**
     * @param {Resource} item - the highlighted item
     */
    highlight(item): void {
        this.highlightedItemChanged$.emit(item);
    }
    
}

import {Component, Input, Output, EventEmitter, ElementRef} from '@angular/core';
import $ from '../libs/jquery';

@Component({
    selector: 'item-header',
    template: `
        <span class="pull-left glyphicon"
            [ngClass]="{
              'glyphicon-chevron-down':  isOpen, 
              'glyphicon-chevron-right': !isOpen}">
        </span>
        {{(item.id)? item.id: "?"}}: {{item.name}}
        <span class="pull-right">
          <img *ngIf="_isType" class="imtip" src="../images/type.png"/>
          <img class="icon" [src]="getResourceIcon()"/>
          <button type="button" *ngIf="options?.showActive" class="btn btn-default btn-header" 
            [ngClass]="{'active': isActive}" (click)="activeItemChanged.emit(item)">
            <span class = "glyphicon" [ngClass]="{'glyphicon-pencil': isActive}"></span>
          </button>
        </span>
  `,
    styles: [`
        .icon {
          height: 16px;
          width: 16px;
          padding: 0;
        }
        .imtip {
          position: relative;
          bottom: 2px;
          left: 4px;
          width: 24px;
          height: 12px;
        } 
        :host{
            display: block;
            padding: 4px;
        }
    `]
})
/**
 * The ItemHeader component configures accordion header for a given resource. The header consists of a left side icon that indicates whether
 * the accordion tab is open or closed, the title formed from the resource's id and name, and the right hand icon that shows
 * the class of the resource. The is an option to include to the header a checkbox with an icon to mark the resource as active.
 */
export class ItemHeader {
    /**
     * {Resource} item    - the resource for which header will be created
     */
    @Input() item;
    /**
     *  {Object} options   - an optional object with boolean options to configure header appearance, i.e., {showActive: true}
     */
    @Input() options = {};
    /**
     *  {Boolean} isOpen   - a boolean flag to indicate whether the item editor panel is open to choose proper left side icon
     */
    @Input() isOpen;
    /**
     * {Boolean} isActive - a boolean flag to indicate whether the item is active to draw or not the 'eye open' icon
     */
    @Input() isActive;

    /**
     * @emits activeItemChanged  - the item has been marked as active
     */
    @Output() activeItemChanged = new EventEmitter();

    _isType = false;

    /**
     * The getResourceIcon function provides an icon for a given resource
     * @param {Resource} item - the resource
     * @returns {DataURI} - the icon for the resource class
     */
    getResourceIcon(): string{
        if (this.item.class === "Lyph" && this.item.axis) {
            return "../images/lyphWithAxis.png";
        }
        if (this.item.class === "Type"){
            return this.item['<--DefinesType'] && this.item['<--DefinesType'][1]
                ? this.item['<--DefinesType'][1].constructor.icon
                : "../images/resource.png";
        }
        return this.item.constructor.icon;
    }

    /**
     *
     * @param {ElementRef} el - the components's underlying native element
     */
    constructor(el: ElementRef) {
        this.el = $(el.nativeElement);
    }

    ngOnInit() {
        this._isType = this.item && (this.item.class === "Type");
        this.el.parent().parent().parent().css("padding", 0);
    }
}

import {Component, Input} from '@angular/core';
import {AbstractResourceList} from "./AbstractResourceList";
import {HighlightService} from './HighlightService.js';
import {ToastyService} from 'ng2-toasty';
import {SetToArray, FilterBy} from "../common/PipeTransformModule";

@Component({
    selector: 'nested-resource-list',
    template:`
    <div class="panel repo-nested">
      <div class="panel-heading"> <label>{{caption}}: </label></div>
      <div class="panel-body" >
        <span  *ngIf = "!(options?.readOnly || options?.headersOnly)">
          <select-input-1 class="pull-left input-select" 
            [item] = "_itemToInclude"
            (updated) = "_itemToInclude = $event"    
            [options] = "possibleValues">
          </select-input-1>
          <button type="button" class="btn btn-default btn-icon" (click)="onIncluded(_itemToInclude)">
            <span class="glyphicon glyphicon-save"></span>
          </button>
        </span>
        
        <toolbar-sort   *ngIf =  "options?.sortToolbar"  [options]="['Name', 'ID', 'Class']" (sorted)="onSorted($event)"></toolbar-sort>
        <toolbar-add    *ngIf = "!(options?.readOnly || options?.headersOnly)"  [options]="types" [transform]="getClassLabel" (added)="onAdded($event)"></toolbar-add>
        <toolbar-filter *ngIf =  "options?.filterToolbar" [options]="['Name', 'ID', 'Class']" [filter]="_searchString" (applied)="onFiltered($event)"></toolbar-filter>
          
        <accordion [closeOthers]="true"
          dnd-sortable-container [dropZones]="_zones" [sortableData]="items">
          <accordion-group *ngFor="let item of items 
            | orderBy : _sortByMode
            | filterBy: [_searchString, _filterByMode]; let i = index" 
                dnd-sortable (onDragStart)="_onDragStart()" (onDragEnd)="_onDragEnd()" [sortableIndex]="i"
                (onOpen) ="openItem = item"
                (onClose)="openItem = null">
 
            <accordion-heading (click)  ="selectedItem = item">
              <item-header [item]="item" 
                [isOpen]       = "item === openItem" 
                (mouseover)    = "highlightedItem = item" 
                (mouseout)     = "unhighlight(item)"
                [ngClass] ="{highlighted: item === highlightedItem, active: item === selectedItem}">
              </item-header>
            </accordion-heading>

