import {NgModule, Component, Input, Output, EventEmitter} from '@angular/core';
import {CommonModule} from '@angular/common';
import {PipeTransformModule, SetToArray, MapToOptions} from '../PipeTransformModule';
import {DropdownModule} from 'ngx-dropdown';
import {FormsModule} from '@angular/forms';
import {SelectModule} from 'angular2-select';

@Component({
    selector: 'select-input',
    template: `
        <div *ngIf="_active">
            <ng-select 
                    [options]    = "options | setToArray | mapToOptions"
                    [(ngModel)]  = "_items"
                    placeholder  = "Select many"
                    [disabled]   = "disabled"
                    [multiple]   = "true"
                    [allowClear] = "true"
                    (selected)   = "_updateValue($event)"
                    (deselected) = "_updateValue($event)">
            ></ng-select>
        </div>
    `,
    styles: [
        `
            :host > div >>> .below {
                height: 30px;
                border-radius: 4px;
            }
        `
    ]
})
/**
 * A component for multiple selection in drop-down list
 */
export class MultiSelectInput {
    /**
     * The set of input items
     * @type {Set<T>|Set}
     */
    @Input()  items   = new Set();
    /**
     * The set of items for selection
     * @type {Set<T>|Set}
     */
    @Input()  options = new Set();
    /**
     * The boolean flag to disable the component
     * @type {boolean}
     */
    @Input()  disabled = false;
    /**
     * The event to signal that the selected item has been changed
     * @type {EventEmitter}
     */
    @Output() updated = new EventEmitter();

    _active = true;
    _externalChange = false;
    _items = [];

    /**
     * Update component parameters in response to external data change
     * @param changes - the object defining input data changes
     */
    ngOnChanges(changes) {
        if (this._externalChange){
            setTimeout(() => { this._active = false }, 0);
            setTimeout(() => { this._active = true  }, 0);
            this._items =  new MapToOptions().transform(new SetToArray().transform(this.items || {}));
        }
        this._externalChange = true;
    }

    /**
     * @param item - added or removed item
     * @private
     */
    _updateValue(item){
        this._externalChange = false;
        this.items = this._items;
        this.updated.emit(this.items);
    }
}

@Component({
    selector: 'select-input-1',
    template:`
        <div *ngIf="_active">
            <ng-select
                    [options]    = "options | setToArray | mapToOptions"
                    [(ngModel)]  = "_items"
                    placeholder  = "Select one"
                    [disabled]   = "disabled"
                    [multiple]   = false
                    [allowClear] = true
                    (selected)   = "_updateValue($event)"
                    (deselected) = "_updateValue($event)">
            ></ng-select>
        </div>
    `,
    styles: [
        `
        :host > div >>> .below {  
            height: 30px;
            border-radius: 4px;
        }
        `
    ]
})
/**
 * A component for a single item selection in drop-down list
 */
export class SingleSelectInput {
    /**
     * The set of input items
     * @type {Set<T>|Set}
     */
    @Input()  item  = new Set();
    /**
     * The set of items for selection
     * @type {Set<T>|Set}
     */
    @Input()  options = new Set();
    /**
     * The boolean flag to disable the component
     * @type {boolean}
     */
    @Input()  disabled = false;
    /**
     * The event to signal that the selected item has been changed
     * @type {EventEmitter}
     */
    @Output() updated = new EventEmitter();

    _active = true;
    _externalChange = false;
    _items = [];

    /**
     * Update component parameters in response to external data change
     * @param {Object} changes - the object defining input data changes
     */
    ngOnChanges(changes) {
        if (this._externalChange){
            setTimeout(() => { this._active = false }, 0);
            setTimeout(() => { this._active = true  }, 0);
            this._items =  new MapToOptions().transform(new SetToArray().transform(this.item));
        }
        this._externalChange = true;
    }

    /**
     * @param item - added or removed item
     * @private
     */
    _updateValue(item){
        this._externalChange = false;
        this.item = this._items;
        this.updated.emit(this.item);
    }
}
/**
 * The CustomSelectModule module, offers pre-configured multiple and single item selection components.
 */
@NgModule({
    imports: [ CommonModule, FormsModule, DropdownModule, PipeTransformModule, SelectModule ],
    declarations: [ MultiSelectInput, SingleSelectInput ],
    exports: [ MultiSelectInput, SingleSelectInput ]
})
export class CustomSelectModule {}

import {NgModule, Pipe, PipeTransform} from '@angular/core';
import isString from 'lodash-bound/isString';
import isUndefined from 'lodash-bound/isUndefined'

@Pipe({name: 'hideClass'})
/**
 * The HideClass pipe, hides resources of given classes.
 */
export class HideClass implements PipeTransform {
    /**
     * @param {Array<Resource>} items - the array of resources to filter
     * @param {Array<string>} classNames - the array of resource classes to hide
     *
     * @returns {Array<Resource>} - the array of filtered resources
     */
    transform(items, classNames = []) {
        return items.filter(x => !classNames.includes(x.class));
    }
}

/**
 * The FilterBy pipe, filters resources to select those that contain a given string in a given field.
 */
@Pipe({name: 'filterBy'})
export class FilterBy implements PipeTransform {
    /**
     * @param {Array<Resource>} items - the array of resources to filter
     * @param {[string, string]} params - an array with two parameters:
     * [0] - the string that the resource field must contain, [1] - the resource field name
     * @returns {Array<Resource>} - the array of filtered resources
     */
    transform(items = [], params) {
        let [searchString, propName] = params;
        if (searchString.length === 0) { return items; }
        return items.filter(item => (typeof(item[propName]) === 'string')
            ? item[propName].toLowerCase().indexOf(searchString.toLowerCase()) !== -1
            : item[propName] === searchString);
    }
}

/**
 * The SetToArray pipe, converts a given set to an array.
 */
@Pipe({name: 'setToArray'})
export class SetToArray implements PipeTransform {
    /**
     * @param {Set} items - the set of items
     * @returns {Array} - the array of items
     */
    transform(items) { return Array.from(items || {}).filter(x => !x::isUndefined()); }
}

/**
 * The OrderBy pipe, sorts given array according to the given sort mode
 */
@Pipe({name: 'orderBy', pure: false})
export class OrderBy implements PipeTransform {
    /**
     *
     * @param {Array} items - the array of items to sort
     * @param {string} sortMode - the sort mode, the property of the item used for sorting
     *  with optional "-" prefix to sort in the descending order.
     * @returns {Array} - the sorted array. If sortedMode equals "unsorted", the function returns the input array without changes.
     */
    transform(items, sortMode) {
        let propName = (sortMode[0] === '-')? sortMode.substring(1): sortMode;
        if (propName === "unsorted") {
            return items;
        } else {
            if (!items || (items.length === 0)) {return items;}
            if (items[0]::isString()){
                return items.sort((a, b) => {
                    if ((sortMode[0] === '-')) {//desc
                        return (a > b) ? -1 : ((a < b) ? 1 : 0);
                    } else {//asc
                        return (a > b) ? 1 : ((a < b) ? -1 : 0);
                    }
                });
            }
            return items.sort((a, b) => {
                if ((sortMode[0] === '-')) {//desc
                    return (a[propName] > b[propName])? -1: ((a[propName] < b[propName])? 1: 0);
                } else {//asc
                    return (a[propName] > b[propName])? 1: ((a[propName] < b[propName])? -1: 0);
                }
            });
        }
    }
}

/**
 * The MapToOptions pipe, maps resources into objects for drop-down lists
 */
@Pipe({name: 'mapToOptions'})
export class MapToOptions implements PipeTransform {
    /**
     * @param {Array<Resource>} items -  the array of resources to convert to the array of selection options
     * which are objects with fields "id" and "text".
     * @returns {Array<{value: string, label: string}>} - the array of drop-down list selection options
     */
    transform(items = []) {
        return items.map(x =>({value: x, label: x.name? x.name: "(Unnamed) " + x.class}))
    }
}


/**
 * The PipeTransformModule module, exports open-physiology helper pipes
 */
@NgModule({
    declarations: [ OrderBy, FilterBy, SetToArray, HideClass, MapToOptions],
    exports: [ OrderBy, FilterBy, SetToArray, HideClass, MapToOptions]
})
export class PipeTransformModule {}

import {NgModule, Component, Input, Output, EventEmitter} from '@angular/core';
import {CommonModule} from '@angular/common';
import {DropdownModule} from 'ngx-dropdown';
//import {Ng2DraggableModule} from 'ng2-draggable';

@Component({
    selector: 'toolbar-propertySettings',
    template: `
        <div class="dropdown pull-right" dropdown [dropdownToggle]="false">
            <button type="button" class="btn btn-default btn-icon" aria-label="Settings" dropdown-open>
                <span class="glyphicon glyphicon-list"></span>
            </button>
            <!--<div draggable="true">-->
            <ul class="dropdown-menu dropdown-menu-right" role="menu" aria-labelledby="Settings"
                dropdown-not-closable-zone>
                <li *ngFor="let option of options">
                    <a class="small" href="#">
                        <input type="checkbox"
                               [checked]="option.selected" (change)="_updateValue(option, $event)"/>&nbsp;
                        <span [style.color]="option.color">{{transform ? transform(option.value) : option.value}}</span>
                    </a>
                </li>
            </ul>
            <!--</div>-->
        </div>
    `
})
/**
 * The ToolbarPropertySettings component, offers a button to choose checkboxes with configuration properties from the drop-down menu.
 *
 * @param {Array<Object>} options - the list of properties to choose from, i.e., item types (resource classes)
 *   A configuration option is an Object with the visible label and the initial value, i.e.,
 * @param {string => string} transform - an optional function that can be used to transform option appearance
 *
 * @emits selectionChanged  - a user changed the value of a checkbox
 *
 */
export class ToolbarPropertySettings {
    @Input()  options = [];
    @Input()  transform: (x: string) => string;
    @Output() selectionChanged = new EventEmitter();

    _updateValue(option, event) {
        option.selected = event.target.checked;
        this.selectionChanged.emit(option);
    }
}

/**
 * The ToolbarSettingsModule module, offers the ToolbarPropertySettings component.
 */
@NgModule({
    imports: [CommonModule, DropdownModule/*, Ng2DraggableModule*/],
    declarations: [ToolbarPropertySettings],
    exports: [ToolbarPropertySettings]
})
export class ToolbarSettingsModule {
}

import {NgModule, Component, ViewChild, EventEmitter, Input, Output} from '@angular/core';
import {CommonModule} from '@angular/common';
import {FormsModule} from '@angular/forms';
import '../libs/jquery';

import {Ng2Bs3ModalModule, ModalComponent} from '../libs/ng2-bs3-modal';

@Component({
    selector: 'modal-window',
    template:`
        <modal #myModal>
            <modal-header [show-close]="true">
                <h4 class="modal-title">Select supertype measurables to replicate</h4>
            </modal-header>
            <modal-body>
                <li *ngFor="let option of _supertypeMeasurables; let i = index">
                    <a class="small" href="#">
                        <input type="checkbox"
                               [(ngModel)]="option.selected"
                               (ngModelChange)="_updateValue(option)"/>&nbsp;
                        {{option.value.name}}</a>
                </li>
            </modal-body>
            <modal-footer>
                <button type="button" class="btn btn-default" data-dismiss="modal">Cancel</button>
                <button type="button" class="btn btn-primary" (click)="close($event)">Ok</button>
            </modal-footer>
        </modal>
    `
})
/**
 * MeasurableGenerator component, provides generation of measurables in a pop-up window.
 *
 * @param {Resource} item - a resource, typically, lyph, which inherits measurables from its type
 * @emits closed - a user closed the modal window
 *
 */
export class MeasurableGenerator{
    @Input()  item;
    @Input()  resourceClasses;
    @Input()  resourceFactory;
    @Output() closed = new EventEmitter();

    //Measurable replication
    _supertypeMeasurables   = [];
    _measurablesToReplicate = new Set();

    @ViewChild('myModal')
    modal: ModalComponent;

    /**
     * Open the modal window with pre-filled measurables of lyph supertypes
     */
    open() {
        let allSupertypeMeasurables = [];
        for (let type of this.item.types) {
            for (let supertype of type.supertypes) {
                if (supertype.definition && supertype.definition.measurables) {
                    let supertypeMeasurables = Array.from(new Set(supertype.definition.measurables.map((item) => item.type)));
                    for (let supertypeMeasurable of supertypeMeasurables) {
                        if (allSupertypeMeasurables.indexOf(supertypeMeasurable) < 0)
                            allSupertypeMeasurables.push(supertypeMeasurable);
                    }
                }
            }
        }
        this._supertypeMeasurables = allSupertypeMeasurables.map(x => {
            return {value: x, selected: this._measurablesToReplicate.has(x)}
        });
        this.modal.open();
    }

    /**
     * Close the modal window
     * @param event - the event object
     */
    close(event) {
        if (this._measurablesToReplicate.size > 0){
            let protoMeasurables = Array.from(this._measurablesToReplicate);
            for (let protoMeasurable of protoMeasurables){
                let newMeasurable = this.resourceFactory(this.resourceClasses.Measuarable.name, protoMeasurable);
                newMeasurable.location = this.item;
            }
        }
        this.modal.close();
        this.closed.emit(event);
    }

    _updateValue(option){
        if ( this._measurablesToReplicate.has(option.value) && !option.selected)
            this._measurablesToReplicate.delete(option.value);
        if (!this._measurablesToReplicate.has(option.value) && option.selected)
            this._measurablesToReplicate.add(option.value);
    }
}

/**
 * The MeasurableGeneratorModule module, offers the MeasurableGenerator component.
 */
@NgModule({
    imports: [ CommonModule, FormsModule, Ng2Bs3ModalModule],
    declarations: [ MeasurableGenerator ],
    exports: [ MeasurableGenerator ]
})
export class MeasurableGeneratorModule {}

import {NgModule, Component, Input, EventEmitter, Output} from '@angular/core';
import {CommonModule} from '@angular/common';
import {FormsModule} from '@angular/forms';
import isUndefined from 'lodash-bound/isUndefined';

@Component({
    "inputs": ["caption", "item", "min", "max", "step"],
    "selector": "template-value",
    "template": `
        <div class="input-control input-control-lg">
            <label for="caption">{{caption}}:</label>

            <div class="btn-group" style="float: left;">
                <button type="button" class="btn btn-default btn-icon"
                        [ngClass]="{'active': item.class === 'Number'}" (click)="_updateValue('Number')">
                    <span class="glyphicon glyphicon-th"></span>
                </button>
                <button type="button" class="btn btn-default btn-icon"
                        [ngClass]="{'active': item.class === 'NumberRange'}" (click)="_updateValue('NumberRange')">
                    <span class="glyphicon glyphicon-transfer"></span>
                </button>
                <button type="button" class="btn btn-default btn-icon"
                        [ngClass]="{'active': item.class === 'BoundedNormalDistribution'}"
                        (click)="_updateValue('BoundedNormalDistribution')">
                    <span class="glyphicon glyphicon-stats"></span>
                </button>
                <button type="button" class="btn btn-default btn-icon"
                        [ngClass]="{'active': item.class === 'UniformDistribution'}"
                        (click)="_updateValue('UniformDistribution')">
                    <span class="glyphicon glyphicon-align-center"></span>
                </button>
            </div>
        </div>

        <div class="input-control input-control-md" *ngIf="item.class === 'Number'">
            <label>Value:</label>
            <input type="number" class="form-control"
                   [min]="min"
                   [max]="max"
                   [step]="step"
                   [(ngModel)]="item.value"
                   (ngModelChange)="updated.emit(item)"/>
        </div>

        <div class="input-control" *ngIf="(item.class === 'NumberRange') || (item.class.endsWith('Distribution'))">
            <label>{{_getLabel()}}:</label>
            <fieldset>
                <div class="input-control input-control-sm">
                    <label for="min">Min: </label>
                    <input type="number" class="form-control"
                           [min]="min"
                           [max]="max"
                           [step]="step"
                           [(ngModel)]="item.min"
                           (ngModelChange)="updated.emit(item)">
                </div>
                <!--Max-->
                <div class="input-control input-control-sm">
                    <label for="max">Max: </label>
                    <input type="number" class="form-control"
                           [min]="min"
                           [max]="max"
                           [step]="step"
                           [(ngModel)]="item.max"
                           (ngModelChange)="updated.emit(item)">
                </div>
                <div *ngIf="item.class === 'BoundedNormalDistribution'" style="display: inline-block">
                    <!--Mean-->
                    <div class="input-control input-control-sm">
                        <label for="mean">Mean: </label>
                        <input type="number" class="form-control"
                               [min]="min"
                               [max]="max"
                               [step]="step"
                               [(ngModel)]="item.mean"
                               (ngModelChange)="updated.emit(item)">
                    </div>
                    <!--Std-->
                    <div class="input-control input-control-sm">
                        <label for="std">Std: </label>
                        <input type="number" class="form-control"
                               [min]="min"
                               [max]="max"
                               [step]="step"
                               [(ngModel)]="item.std"
                               (ngModelChange)="updated.emit(item)">
                    </div>
                </div>
            </fieldset>
        </div>
    `,
    "styles": [`
        input {width: 60px;}
        .form-control {
          height: 30px;
          box-shadow: none!important;
        }
        .form-control:focus  {
          border: 2px solid #ccc;
          box-shadow: none!important;
        }

    `],
})
/**
 * The TemplateValueComponent, provides fields for editing typedDistribution schema
 */
export class TemplateValueComponent{
    /**
     * @property {Object} item - the object specifying a value, range or distribution:
     * { 'value': { type: 'number' },
     *   'min':   { type: 'number' },
     *   'max':   { type: 'number' },
     *   'mean':  { type: 'number' },
     *   'std':   { type: 'number' },
     *   'class': { type: 'string', required: true }
     *    Class values: 'UniformDistribution' | 'BoundedNormalDistribution' | 'Number' | 'NumberRange'
     */
    @Input() item = {value: 0, class: 'Number'};
    /**
     * @property {number} min - the default value of the field "min"
     */
    @Input() min = 0;
    /**
     * @property {number} max - the default value of the field "max"
     */
    @Input() max = 10;
    /**
     * @property {number} step - the default value of the field "step"
     */
    @Input() step: number = 1;

    /**
     * The event signalling that the object has been changed
     * @type {EventEmitter}
     */
    @Output() updated = new EventEmitter();

    /**
     * Initialize the component
     */
    ngOnInit(){
        if (this.item::isUndefined()){
            this.item = {value: 0, class: 'Number'};
        }
        if (this.item.class::isUndefined()){
            this.item.class = 'Number';
        }
    }

    _updateValue(type){
        this.item.class = type;
        this.updated.emit(this.item);
    }

    _getLabel(){
        return
            (this.item.class === 'NumberRange')? 'Range':
            (this.item.class === 'BoundedNormalDistribution')
                ? 'Bounded normal distribution'
                : 'Uniform distribution';
    }

}

/**
 * The TemplateValueModule module, offers the TemplateValueComponent component.
 */
@NgModule({
    imports: [ CommonModule, FormsModule ],
    declarations: [ TemplateValueComponent ],
    exports: [ TemplateValueComponent ]
})
export class TemplateValueModule {}

import $ from 'jquery';
export default $;
window.$ = $;
window.jQuery = $;

import './jquery';
import 'bootstrap';
export * from 'ng2-bs3-modal/ng2-bs3-modal';

import {Input, Output, EventEmitter} from '@angular/core';
import {HighlightService} from './HighlightService.js';
import {ToastyService} from 'ng2-toasty';

/**
 * The AbstractResourceList class implements methods common for upper level and nested resource lists.
 */
export class AbstractResourceList {
    /**
     * @type {string} caption           - the header of the list
     */
    @Input() caption = "Resources";

    /**
     * A function that creates resources that are handled by model library
     */
    @Input() resourceFactory;

    /**
     * @type {Object} resourceClasses          - the open-physiology model
     */
    @Input() resourceClasses;

    /**
     * @type {Array<Resource>} items     - the list of displayed resources
     */
    @Input() items = [];

    /**
     * @type {Object} - visualization options
     */
    @Input() options = {};

    @Input('selectedItem') set selectedItem(item) {
        if (this._selectedItem !== item) {
            this._selectedItem = item;
            this.selectedItemChange.emit(item);
        }
    }
    get selectedItem() { return this._selectedItem; }
    @Input('highlightedItem') set highlightedItem(item) {
        if (this._highlightedItem !== item) {
            this._highlightedItem = item;
            this.highlightedItemChange.emit(item);
            this._highlightService.highlight(this._highlightedItem);
        }
    }
    get highlightedItem() { return this._highlightedItem; }

    /**
     * @emits added                 - a new item added to the list
     */
    @Output() added = new EventEmitter();
    /**
     * @emits removed               - an item removed from the list
     */
    @Output() removed = new EventEmitter();
    /**
     * @emits updated               - an item in the list has been updated
     */
    @Output() updated = new EventEmitter();
    /**
     * @emits selectedItemChange    - the selected item changed
     */
    @Output() selectedItemChange = new EventEmitter();
    /**
     * @emits highlightedItemChange - the highlighted item changed
     */
    @Output() highlightedItemChange = new EventEmitter();

    _selectedItem    = null;
    _highlightedItem = null;
    _openItem        = null;
    _typeNames       = [];
    _sortByMode      = "unsorted";
    _filterByMode    = "Name";
    _searchString    = "";

    /**
     * The constructor of the component
     * @param {HighlightService} highlightService - the service that notifies nested components about currently highlighted item
     * @param {ToastyService} toastyService - the service for showing notifications and error messages
     */
    constructor(highlightService: HighlightService, toastyService: ToastyService) {
        this._highlightService = highlightService;
        this._toastyService = toastyService;
        this._hs = this._highlightService.highlightedItemChanged$.subscribe(item => {
            if (this.items.includes(item) && this._highlightedItem !== item){
                this._highlightedItem = item;
            }
        })
    }

    /**
     * Initialize the nested resource list: choose selected item and default parameters (e.g., visible classes)
     */
    ngOnInit() {
        if (!this.items) { this.items = []; }

        if (this.items[0] || !this.selectedItem) {
            this.selectedItem = this.items[0];
        }
    }

    /**
     * On component destroy, unsubscribe from model library data handling events
     */
    ngOnDestroy() {
        if (this._hs) {
            this._hs.unsubscribe();
        }
    }

    _unhighlight(item) {
        if (this.highlightedItem === item) {
            this.highlightedItem = null;
        }
    }

    /**
     * Choose a resource to show in the open editor
     * @param {Resource} item - the resource with the open editor
     */
    set openItem(item){
        if (this._openItem !== item) {
            this._openItem = item;
        }
    }

    /**
     * @returns {Resource} - the resource with the open editor
     */
    get openItem(){
        return this._openItem;
    }

    /* Event processing */

    /**
     * Event handler for the sorting mode change event
     * @param {string} prop - the sorting mode
     */
    _onSorted(prop) {
        this._sortByMode = prop.toLowerCase();
    }

    /**
     * Event handler for the filter change event
     * @param {Object} config - the parameters for filtering, mode and search string.
     */
    _onFiltered(config) {
        this._filterByMode = config.mode.toLowerCase();
        this._searchString = config.filter;
    }

    /**
     * Event handler for the 'save' ('commit') event
     * @param {Resource} item - the resource to save (commit)
     */
    _onSaved(item) {
        this.updated.emit(this.items);
        if (item === this.selectedItem) {
            this.selectedItemChange.emit(this.selectedItem);
        }
    }

    /**
     * Event handler for the 'delete' event
     * @param {Resource} item - he resource to delete
     */
    _onRemoved(item) {
        if (!this.items) return;
        let index = this.items.indexOf(item);
        if (index > -1) this.items.splice(index, 1);
        if (item === this.selectedItem) {
            if (this.items.length > 0){
                this.selectedItem = this.items[0];
            }
            else {
                this.selectedItem = null;
            }
        }
        item.delete();
        this.removed.emit(item);
        this.updated.emit(this.items);
    }

    /**
     * Event handler for the 'create new resource' event
     * @param {string} clsName - the type of the resource to create
     */
    _onAdded(clsName) {
        let options = {};
        if (clsName === "LyphWithAxis") {
            clsName = this.resourceClasses.Lyph.name;
            options.createAxis = true;
        }
        if (clsName === this.resourceClasses.Lyph.name) {
            options.createRadialBorders = true;
        }

        let newItem;
        if (this.resourceFactory){
            newItem = this.resourceFactory(clsName, {name: `New ${clsName}`}, options);
            if (clsName === this.resourceClasses.Material.name) {
                let newType = this.resourceFactory(this.resourceClasses.Type.name,
                    {name: newItem.name, definition: newItem});
                newItem.p('name').subscribe(newType.p('name'));
            }
        } else {
            let cls = this.resourceClasses[clsName];
            newItem = cls.new({name: `New ${clsName}`}, options);
            if (clsName === this.resourceClasses.Material.name) {
                this.resourceClasses.Type.new({name: newItem.name, definition: newItem});
            }
            this._toastyService.error("The resource factory is not provided! The created resource is a placeholder!");
        }

        this.items.push(newItem);
        this.updated.emit(this.items);
        this.added.emit(newItem);
        this.selectedItem = newItem;
    }

    /**
     * The getClassLabel function provides an human readable label for open-physiology class
     * @param {Resource} clsName - the resource class name
     * @returns {string} - the human readable label
     */
    _getClassLabel(clsName){
        if (!clsName) { return ""; }
        let label = clsName;
        label = label.replace(/([a-z])([A-Z])/g, '$1 $2');
        label = label[0].toUpperCase() + label.substring(1).toLowerCase();
        return label;
    }

}

